import { useEffect, useState } from "react";
import axios from "axios";
import { useGetUserID } from "../hooks/useGetUserID";
import { useCookies } from "react-cookie";

export const Home = () => {
  const [memories, setMemories] = useState([]);
  const [savedMemories, setSavedMemories] = useState([]);
  const [cookies, _] = useCookies(["access_token"]);
  const userID = useGetUserID();

  useEffect(() => {
    const fetchMemories = async () => {
      try {
        const response = await axios.get("http://localhost:3001/memories");
        setMemories(response.data);
      } catch (err) {
        console.error(err);
      }
    };

    const fetchSavedMemories = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/memories/savedMemories/ids/${userID}`);
        setSavedMemories(response.data.savedMemories);
      } catch (err) {
        console.error(err);
      }
    };

    fetchMemories();
    if (cookies.access_token) fetchSavedMemories();
  }, []);

  const saveMemory = async (memoryID) => {
    try {
      const response = await axios.put("http://localhost:3001/memories", {
        memoryID,
        userID,
      }, {
        headers: { authorization: cookies.access_token }
      });
      setSavedMemories(response.data.savedMemories);
    } catch (err) {
      console.error(err);
    }
  };

  const isMemorySaved = (id) => savedMemories.includes(id);

  return (
    <div>
      <h1>Memories</h1>
      <ul>
        {memories.map((memory) => (
          <li key={memory._id}>
            <div>
              <h2>{memory.name}</h2>
              <button onClick={() => saveMemory(memory._id)} disabled={isMemorySaved(memory._id)}>
                {isMemorySaved(memory._id) ? "Saved" : "Save"}
              </button>
            </div>
            <div className="descriptions">
              {memory.descriptions.map((desc, i) => (
                <p key={i}>{desc}</p>
              ))}
            </div>
            <img src={memory.imageUrl} alt={memory.name} />
            <p>Time Spent: {memory.timeSpent}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};
