import { useEffect, useState } from "react";
import axios from "axios";
import { useGetUserID } from "../hooks/useGetUserID";
import { useCookies } from "react-cookie";

export const Home = () => {
  const [memories, setMemories] = useState([]);
  // Initialize savedMemories as an empty array explicitly
  const [savedMemories, setSavedMemories] = useState([]);
  const [cookies, _] = useCookies(["access_token"]);
  const userID = useGetUserID();

  useEffect(() => {
    const fetchMemories = async () => {
      try {
        const response = await axios.get("http://localhost:3001/memories");
        setMemories(response.data);
      } catch (err) {
        console.error(err);
      }
    };

    const fetchSavedMemories = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3001/memories/savedMemories/ids/${userID}`
        );
        // Ensure response.data.savedMemories is an array before setting state
        setSavedMemories(response.data.savedMemories || []);
      } catch (err) {
        console.error(err);
        // Set to empty array in case of an error fetching saved memories
        setSavedMemories([]);
      }
    };

    fetchMemories();
    if (cookies.access_token && userID) { // Also check if userID is available
        fetchSavedMemories();
    }
  }, [cookies.access_token, userID]); // Add dependencies

  const saveMemory = async (memoryID) => {
    try {
      const response = await axios.put(
        "http://localhost:3001/memories",
        {
          memoryID,
          userID,
        },
        {
          headers: { authorization: cookies.access_token },
        }
      );
      // Ensure response.data.savedMemories is an array before setting state
      setSavedMemories(response.data.savedMemories || []);
    } catch (err) {
      console.error(err);
    }
  };

  // Safely check if savedMemories is an array before calling includes
  const isMemorySaved = (id) => Array.isArray(savedMemories) && savedMemories.includes(id);

  return (
    <div>
      <h1>Memories</h1>
      <ul>
        {memories.map((memory) => (
          <li key={memory._id}>
            <div>
              <h2>{memory.name}</h2>
              {/* Only render the button if userID is available */}
              {userID && (
                <button
                  onClick={() => saveMemory(memory._id)}
                  disabled={isMemorySaved(memory._id)}
                >
                  {isMemorySaved(memory._id) ? "Saved" : "Save"}
                </button>
              )}
            </div>
            <div className="descriptions">
              {/* Add a check before mapping over descriptions */}
              {memory.descriptions && memory.descriptions.map((desc, i) => (
                <p key={i}>{desc}</p>
              ))}
            </div>
            <img src={memory.imageUrl} alt={memory.name} />
            <p>Time Spent: {memory.timeSpent}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};
