import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";

export const Home = () => {
    const [memories, setMemories] = useState([]);
    useEffect(() => {
        const fetchMemory = async () => {
            try{
                const response = await axios.get("http://localhost:3001/memories");
                setMemories(response.data);
            } catch (err) {
                console.error(err);
            }
        } 

        fetchMemory();
    }, []);

    const saveMemory = async (memoryID) =>{
        try{
            const response = await axios.put("http://localhost:3001/memories");
            console.log(response);
        } catch (err) {
            console.error(err);
        }
    };

    return (
        <div>
        <h1>Memories</h1>
        <ul>
            {memories.map((memory) =>(
                <li key = {memory._id}>
                    <div>
                        <h2>{memory.name}</h2>
                        <button onClick={() => saveMemory(memory._id)}>Save</button>
                    </div>
                    <div className="descriptions">
                        <p>{memory.descriptions}</p>
                    </div>
                    <img src={memory.imageUrl} alt={memory.name}/>
                    <p>Time Spent : {memory.timeSpent}</p>
                </li>
            ))}
        </ul>
        </div>
    );
};
